C++<pre><code>vector&lt;int&gt; preorderTraversal(TreeNode* root)
{
    vector&lt;int&gt; result;
    if (root == nullptr)
        return result;
    stack&lt;TreeNode*&gt; st_TreeNode;
    TreeNode* node = root;
    while (node != nullptr || !st_TreeNode.empty())
    {
        while (node != nullptr)
        {
            st_TreeNode.push(node);
            result.push_back(node-&gt;val);
            node = node-&gt;left;
        }
        if (!st_TreeNode.empty())
        {
            node = st_TreeNode.top();
            st_TreeNode.pop();
            node = node-&gt;right;
        }
    }
    return result;
}
